sp_widget.client_script:
function($rootScope,$scope) {
	/* widget controller */
	var c = this;
	$rootScope.$on('updateNote', function(event,data) {	
		console.log('Client script su Lista per elem.:' + $rootScope.noteID);
		c.server.get({
			action: 'getNote',
			noteID: $rootScope.noteID
		}).then(function(r) {
			console.log('Di ritorno da Server script Lista. Titolo=' + r.data.note.title );
			var idx=1;
			c.data.notes[idx].title = r.data.note.title;
			c.data.notes[idx].note = r.data.note.note;
		//	c.data.noteID = r.data.note.sys_id;
		});
	});
	c.selectItem = function(idx) {
		var id = c.data.notes[idx].sys_id;
		console.log('Note ID: ' + id);
		$rootScope.noteID = id;
		$rootScope.$emit('selectNote', id);
	};
}

sp_widget.script:
(function() {
	//create an array to populate without notes
	data.notes = [];
	var noteGR = new GlideRecord('x_snc_createnotes_note');
	noteGR.addQuery('user', gs.getUser().getID());
	noteGR.orderByDesc('sys_created_on');
	noteGR.query();
	while (noteGR.next()) {
		var noteObj = {};
		//use service portal helper method to get some display values
		$sp.getRecordDisplayValues(noteObj, noteGR, 'number,title,sys_id');
		//get the first 20 characters of the description
		noteObj.note = noteGR.getValue('note').slice(0,13);
		//push the populated obj into the array
		data.notes.push(noteObj);
	}

if (input && input.noteID) {
		var note = new GlideRecord('x_snc_createnotes_note');
		gs.addInfoMessage("server script Lista:" + input.action + " per " + input.noteID);
		if (note.get(input.noteID)) {
			if (input.action == 'getNote') {
				data.note = {};
				var noteObj = {};
				$sp.getRecordValues(data.note, note, "title, note, sys_id");
				noteObj.note = noteGR.getValue('note').slice(0,13);
				data.notes.push(noteObj);
				//		gs.addInfoMessage("server script Lista/2:" + noteObj.note);
			}
		}
	}

